import React from "react";
import Input from "../Input/Input";
import MenuButton from "../MenuButton/MenuButton";

const InputForm = ({
  setIsSelectedMenu,
  onSubmit,
  onInput,
  newGame,
  name,
  room,
}) => {
  if (newGame) {
    return (
      <div className="input-container">
        <Input
          name="name"
          placeholder="Your Name..."
          onChange={onInput}
          value={name}
        />
        <div className="nav-container">
          <MenuButton
            type="nav-back"
            choice="back"
            onSelect={() => setIsSelectedMenu(false)}
            label="Back"
          />
          <MenuButton
            type="nav-forward"
            choice="submit"
            onSelect={onSubmit}
            label="Let's Go"
