import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";

import "./GamePage.scss";
import BoardRender from "../BoardRender/BoardRender";
import "./fontawesome/css/font-awesome.css";

// query string
import qs from "qs";
// socket.io client
const io = require("socket.io-client");
const socket = io("http://localhost:5000");

export default function GamePage() {
  // main state
  const [waiting, setWaiting] = useState(true);
  const [roomID, setRoomID] = useState("");
  const [name, setName] = useState("");
  const [errorJoin, setErrorJoin] = useState(false);
  const [playerID, setPlayerID] = useState("");
  const [sign, setSign] = useState("");
  const [board, setBoard] = useState([]);
  const [turn, setTurn] = useState(null);
  const [enemy, setEnemy] = useState([]);
  const [endGame, setEndGame] = useState(false);
  const [currentScore, setCurrentScore] = useState(0);
  const [enemyScore, setEnemytScore] = useState(0);

  useEffect(() => {
    //get current room and name
    const { room, name } = qs.parse(window.location.search, {
      ignoreQueryPrefix: true,
    });
    //handle error join
    if (!name || !room) {
      setErrorJoin(true);
    }
    setRoomID(room);
    setName(name);
  }, []);

  useEffect(() => {
    if (roomID) {
      socket.emit("newJoinToTheRoom", { roomID, name });
    }
  }, [roomID]);

  socket.on("initSignAndId", ({ id, sign }) => {
    setSign(sign);
    setPlayerID(id);
  });

  // when two players in the room start the game
  socket.on("startTheGame", ({ turn, board, waiting, players }) => {
    const isCurrentTurn = turn === sign;
    const enemyPlayer = players.filter((player) => !player.includes(playerID));
    setBoard(board);
    setTurn(isCurrentTurn);
    setWaiting(waiting);
    setEnemy(enemyPlayer);
  });

  // update data from server
  socket.on("updateBoard", ({ board, currentTurn, signWinner }) => {
    const isCurrentTurn = currentTurn === sign;
    setBoard(board);
    setTurn(false);
    if (signWinner) {
      setEndGame(true);
    } else {
      setTurn(isCurrentTurn);
    }
  });

  const handleClick = (e, i) => {
    if (turn) {
      console.log(e.target);
      socket.emit("playerMove", { i, sign, roomID });
    } else {
      console.log("CANT");
    }
  };

  const createUniqueRoomID = () =>
    Date.now().toString(36) + Math.random().toString(36).substr(2);

  useEffect(() => {}, [board]);

  // Render

  const classNameOfTheCell = (i, el) => {
    const notSelected =
      i === 0
        ? "cell bottom right"
        : i === 1
        ? "cell bottom"
        : i === 2
        ? "cell bottom left"
        : i === 3
        ? "cell bottom right"
        : i === 4
        ? "cell bottom"
        : i === 5
        ? "cell bottom left"
        : i === 6
        ? "cell right"
        : i === 7
        ? "cell right"
        : "cell";

    return `${notSelected} `;
  };

  if (errorJoin) {
    return <Redirect to={`/`} />;
  } else if (waiting) {
    return <h3>{`Waiting another player... Copy game ID => ${roomID}`}</h3>;
  } else {
    return (
      <div className="game-container">
        <div>
          <div className="board-Ñontainer">
            <div className="turn">
              {turn ? "Your turn" : "Wait for the opponent's move"}
            </div>
            {board.map((el, i) => {
              return (
                <div
                  key={createUniqueRoomID()}
                  className={classNameOfTheCell(i, el)}
                  onClick={(e) => handleClick(e, i)}>
                  <div className="span-container">
                    <span
                      className={
                        el === "X"
                          ? "fa fa-times"
                          : el === "O"
                          ? "fa fa-circle-o"
                          : ""
                      }
                      aria-hidden="true"></span>
                  </div>
                </div>
              );
            })}
            <div className="board-score">Score board</div>
            <div className="container-score">
              <div className="current-score">
                <h3 className="currentName"> You</h3>
                <span className="score-player">{currentScore}</span>
              </div>
              <div className="enemy-score">
                <h3 className="opponentName"> Oponent </h3>
                <span className="score-player">{enemyScore}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
