import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";

import "./GamePage.scss";
import BoardRender from "../BoardRender/BoardRender";
import "./fontawesome/css/font-awesome.css";

// query string
import qs from "qs";
// socket.io client
const io = require("socket.io-client");
const socket = io("http://localhost:5000");

export default function GamePage() {
  // main state
  const [waiting, setWaiting] = useState(true);
  const [roomID, setRoomID] = useState("");
  const [name, setName] = useState("");
  const [errorJoin, setErrorJoin] = useState(false);
  const [playerID, setPlayerID] = useState("");
  const [sign, setSign] = useState("");
  const [board, setBoard] = useState([]);
  const [turn, setTurn] = useState(null);
  const [enemy, setEnemy] = useState([]);
  const [endGame, setEndGame] = useState(false);
  const [currentScore, setCurrentScore] = useStaet(0);
  const [enemyScore, setEnemytScore] = useState(0);

  useEffect(() => {
    //get current room and name
    const { room, name } = qs.parse(window.location.search, {
      ignoreQueryPrefix: true,
    });
    //handle error join
    if (!name || !room) {
      setErrorJoin(true);
    }
    setRoomID(room);
    setName(name);
  }, []);

  useEffect(() => {
    if (roomID) {
      socket.emit("newJoinToTheRoom", { roomID, name });
    }
  }, [roomID]);

  socket.on("initSignAndId", ({ id, sign }) => {
    setSign(sign);
    setPlayerID(id);
  });

  // when two players in the room start the game
  socket.on("startTheGame", ({ turn, board, waiting, players }) => {
    const isCurrentTurn = turn === sign;
    const enemyPlayer = players.filter((player) => !player.includes(playerID));
    setTurn(isCurrentTurn);
    setBoard(board);
    setWaiting(waiting);
    setEnemy(enemyPlayer);
  });

  // update data from server
  socket.on("updateBoard", ({ board, currentTurn, signWinner }) => {
    const isCurrentTurn = currentTurn === sign;
    console.log(board, turn);
    setBoard(board);
    if (signWinner) {
      setEndGame(true);
      setTurn(false);
    } else {
      setTurn(isCurrentTurn);
    }
  });

  // when player win
  socket.on("win", ({ signWinner }) => {
    let winnerName = "";
    if (sign === signWinner) {
      winnerName = name;
    }
    setTurn(false);
    alert(`Congratulations! ${winnerName} is a winner!`);
  });

  const handleClick = (e, i) => {
    if (turn) {
      console.log(e.target);
      socket.emit("playerMove", { i, sign, roomID });
    } else {
      console.log("CANT");
    }
  };

  // Render

  const classNameOfTheCell = (i, el) => {
    const notSelected =
      i === 0
        ? "cell bottom right"
        : i === 1
        ? "cell bottom"
        : i === 2
        ? "cell bottom left"
        : i === 3
        ? "cell bottom right"
        : i === 4
        ? "cell bottom"
        : i === 5
        ? "cell bottom left"
        : i === 6
        ? "cell right"
        : i === 7
        ? "cell right"
        : "cell";
    // const selected =
    //   el === "X" ? "fa fa-times" : el === "O" ? "fa fa-circle-o" : "";
    return `${notSelected} `;
  };

  if (errorJoin) {
    return <Redirect to={`/`} />;
  } else {
    console.log(enemy);
    return (
      <>
        <div>
          {waiting ? (
            <h3>{`Waiting another player... Copy game ID => ${roomID}`}</h3>
          ) : null}
          <div className="turn">
            {turn ? "Your turn" : "Wait for the opponent's move"}
          </div>
          <div className="board-Ñontainer">
            {board.map((el, i) => {
              return (
                <div
                  className={classNameOfTheCell(i, el)}
                  onClick={(e) => handleClick(e, i)}>
                  <div className="span-container">
                    <span
                      className={
                        el === "X"
                          ? "fa fa-times"
                          : el === "O"
                          ? "fa fa-circle-o"
                          : ""
                      }
                      aria-hidden="true"></span>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
        <div className="score-board">Score board</div>;
        <div className="current-score"></div>
        <div className="enemy-score"></div>
      </>
    );
  }
}
