import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";

import "./GamePage.scss";
import BoardRender from "../BoardRender/BoardRender";

// query string
import qs from "qs";
// socket.io client
const io = require("socket.io-client");
const socket = io("http://localhost:5000");

export default function GamePage() {
  const [waiting, setWaiting] = useState(true);
  const [roomID, setRoomID] = useState("");
  const [name, setName] = useState("");
  const [errorJoin, setErrorJoin] = useState(false);
  const [playerID, setPlayerID] = useState("");
  const [sign, setSign] = useState("");
  const [board, setBoard] = useState([]);
  const [turn, setTurn] = useState(null);
  const [enemy, setEnemy] = useState([]);

  useEffect(() => {
    //get current room and name
    const { room, name } = qs.parse(window.location.search, {
      ignoreQueryPrefix: true,
    });
    //handle error join
    if (!name || !room) {
      setErrorJoin(true);
    }
    setRoomID(room);
    setName(name);
  }, []);

  useEffect(() => {
    if (roomID) {
      socket.emit("newJoinToTheRoom", { roomID, name });
      console.log(roomID);
      console.log(name);
    }
  }, [roomID]);

  socket.on("initSignAndId", ({ id, sign }) => {
    setSign(sign);
    setPlayerID(id);
  });

  socket.on("startTheGame", ({ turn, board, waiting, players }) => {
    const isCurrentTurn = turn === sign;
    const enemy = players.filter((player) => !player.includes(playerID));
    setTurn(isCurrentTurn);
    setBoard(board);
    setWaiting(waiting);
    setEnemy(enemy);
  });

  useEffect(() => {
    console.log("sign", sign);
    console.log("playerID", playerID);
    console.log("board", board);
    console.log("turn", turn);
    console.log(enemy);
  }, [sign]);

  // Render

  if (errorJoin) {
    return <Redirect to={`/`} />;
  } else {
    return (
      <div className="container-board">
        <h2>{`Hello ${name}!`}</h2>
        {waiting ? (
          <h3>{`Waiting another player... Copy game ID => ${roomID}`}</h3>
        ) : null}
      </div>
    );
  }
}
