import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";

import "./GamePage.scss";
import BoardRender from "../BoardRender/BoardRender";

// query string
import qs from "qs";
// socket.io client
const io = require("socket.io-client");
const socket = io("http://localhost:5000");

export default function GamePage() {
  const [waiting, setWaiting] = useState(true);
  const [roomID, setRoomID] = useState("");
  const [name, setName] = useState("");
  const [errorJoin, setErrorJoin] = useState(false);

  useEffect(() => {
    //get current room and name
    const { room, name } = qs.parse(window.location.search, {
      ignoreQueryPrefix: true,
    });
    setRoomID(room);
    setName(name);

    socket.emit("newJoinToTheRoom", { roomID, name });
    console.log(roomID);

    //handle error join
    socket.on("joinError", () => {
      setErrorJoin(true);
    });
  }, []);

  console.log(roomID);

  // Render

  if (errorJoin) {
    alert("Error");
  } else {
    return (
      <div className="container-board">
        <h2>{`Hello ${name}!`}</h2>
        {waiting ? (
          <h3>{`Waiting another player... Copy game ID => ${roomID}`}</h3>
        ) : null}
      </div>
    );
  }
}
