import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";

import "./GamePage.scss";
import BoardRender from "../BoardRender/BoardRender";

// query string
import qs from "qs";
// socket.io client
const io = require("socket.io-client");
const socket = io("http://localhost:5000");

export default function GamePage() {
  // state
  const [room, setRoom] = useState("");
  const [errorJoinedRoom, setErrorJoinedRoom] = useState(false);
  const [waitSecondPlayer, setWaitSecondPlayer] = useState(false);
  const [socketID, setSocketID] = useState(null);
  const [mark, setMark] = useState("O");
  const [turn, setTurn] = useState(true);
  const [opponent, setOpponent] = useState([]);
  const [end, setEnd] = useState(false);
  const [gameState, setGameState] = useState([]);
  const [board, setBoard] = useState(new Array(9).fill(null));

  useEffect(() => {
    console.log("NEW");
    //get current room and name
    const { room, name } = qs.parse(window.location.search, {
      ignoreQueryPrefix: true,
    });
    setRoom(room);
    socket.emit("joinedNewRoom", { room, name });

    // asigning id and mark
    socket.on("assignID", ({ id, playerMark }) => {
      console.log(id, playerMark);
      setMark(playerMark);
      setSocketID(id);
    });

    // creating game (board, players ...)
    socket.on("startTheGame", ({ stateOfTheGame, players }) => {
      setWaitSecondPlayer(false);
      startGame(stateOfTheGame, players);
    });

    socket.on("update", ({ stateOfTheGame, turn }) => {
      handleUpdateGame(stateOfTheGame, turn);
      console.log("UODATE", stateOfTheGame);
    });
  });

  const handleUpdateGame = (state, turn) => {
    setBoard(state);
    setTurn(turn);
    console.log("update", board);
  };

  // assigning board state and players data
  const startGame = (state, players) => {
    const player2 = players.filter((el) => el.id !== socketID)[0][1];
    setOpponent([player2, 0]);
    setBoard(state);
    console.log(board);
  };

  const handleClick = (id) => {
    console.log("Board", board[id]);
    console.log("Click", id, room, mark);
    socket.emit("move", { room, mark, id });
  };

  const renderBoard = (i) => {
    console.log(board[i]);
    return (
      <BoardRender
        key={i}
        value={board[i]}
        player={mark}
        end={end}
        id={i}
        onClick={() => handleClick(i)}
        turn={turn}
      />
    );
  };

  const boardArray = [];
  for (let i = 0; i < 9; i++) {
    const newSquare = renderBoard(i);
    boardArray.push(newSquare);
  }
  // Render
  if (errorJoinedRoom) return <Redirect to={`/`} />;
  else {
    return (
      <div className="container-board">
        <div className="board">
          {boardArray}

          {/* {Array(9)
            .fill(null)
            .map((el, id) => {
              return (
                <button
                  key={id}
                  className="cell"
                  onClick={() => handleClick(id)}></button>
              );
            })} */}
        </div>
      </div>
    );
  }
}
