import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";

// query string
import qs from "qs";
// socket.io client
const io = require("socket.io-client");
const socket = io("http://localhost:5000");

export default function GamePage() {
  // state
  const [room, setRoom] = useState("");
  const [errorJoinedRoom, setErrorJoinedRoom] = useState(false);
  const [waitSecondPlayer, setWaitSecondPlayer] = useState(false);

  useEffect(() => {
    console.log("NEW");
    //get current room and name
    const { room, name } = qs.parse(window.location.search, {
      ignoreQueryPrefix: true,
    });
    setRoom(room);
    socket.emit("joinedNewRoom", { room, name });
    socket.on("errorJoinedRoom", setErrorJoinedRoom(true));
  }, []);

  // Render
  if (errorJoinedRoom) return <Redirect to={`/`} />;
  else return <div>Game Page</div>;
}
