import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";

import "./GamePage.scss";
import BoardRender from "../BoardRender/BoardRender";

// query string
import qs from "qs";
// socket.io client
const io = require("socket.io-client");
const socket = io("http://localhost:5000");

export default function GamePage() {
  const [waiting, setWaiting] = useState(true);
  const [roomID, setRoomID] = useState("");
  const [name, setName] = useState("");
  const [errorJoin, setErrorJoin] = useState(false);
  const [playerID, setPlayerID] = useState("");
  const [sign, setSign] = useState("");
  const [board, setBoard] = useState([]);
  const [turn, setTurn] = useState(null);
  const [enemy, setEnemy] = useState([]);

  useEffect(() => {
    //get current room and name
    const { room, name } = qs.parse(window.location.search, {
      ignoreQueryPrefix: true,
    });
    //handle error join
    if (!name || !room) {
      setErrorJoin(true);
    }
    setRoomID(room);
    setName(name);
  }, []);

  useEffect(() => {
    if (roomID) {
      socket.emit("newJoinToTheRoom", { roomID, name });
    }
  }, [roomID]);

  socket.on("initSignAndId", ({ id, sign }) => {
    setSign(sign);
    setPlayerID(id);
  });

  socket.on("startTheGame", ({ turn, board, waiting, players }) => {
    const isCurrentTurn = turn === sign;
    const enemyPlayer = players.filter((player) => !player.includes(playerID));
    setTurn(isCurrentTurn);
    setBoard(board);
    setWaiting(waiting);
    setEnemy(enemyPlayer);
  });

  const handleClick = (i) => {
    socket.emit("playerMove", { i, sign });
  };

  // Render

  const classNameOfTheCell = (i) => {
    return i === 0
      ? "cell bottom right"
      : i === 1
      ? "cell bottom"
      : i === 2
      ? "cell bottom left"
      : i === 3
      ? "cell bottom right"
      : i === 4
      ? "cell bottom"
      : i === 5
      ? "cell bottom left"
      : i === 6
      ? "cell right"
      : i === 7
      ? "cell right"
      : "cell";
  };

  if (errorJoin) {
    return <Redirect to={`/`} />;
  } else {
    return (
      <div>
        <h2>{`Hello ${name}!`}</h2>
        {waiting ? (
          <h3>{`Waiting another player... Copy game ID => ${roomID}`}</h3>
        ) : null}
        <div className="board-Ñontainer">
          {board.map((el, i) => {
            return (
              <div
                className={classNameOfTheCell(i)}
                onClick={() => handleClick(i)}></div>
            );
          })}
        </div>
      </div>
    );
  }
}
